package smart.api;/** * @ClassName   : HostConfigContext * @Description : 主机配置上下文* @author      : dwg* @date        : Sep 22, 2013 10:05:50 AM *  */public class HostConfigContext {	private HostConfig hostConfig;		public HostConfigContext(HostConfig hostConfig) {		this.hostConfig = hostConfig;	}	public HostConfig getHostConfig() {		return hostConfig;	}	public void setHostConfig(HostConfig hostConfig) {		this.hostConfig = hostConfig;	}		public String getAPIHost(){		return hostConfig.getHostInfo();			}	}