package smart.api;/** * @ClassName   : ServiceDeskHostConfig * @Description : 服务台工程主机配置。* @author      : dwg* @date        : Sep 22, 2013 10:16:11 AM *  */public class ServiceDeskHostConfig  implements HostConfig{		private static  String host="http://10.10.152.20:9080";	private static  String projectName="itsm";		public String getHost(){		return host;	}		public String getProjectName(){		return projectName;	}		public String getHostInfo() {		StringBuilder hostInfo=new StringBuilder().append(host).append("/").append(projectName);		return hostInfo.toString();	}		}