package smart.api.host;/** * @ClassName   : HostConfig * @Description : 主机配置，这里将主机IP、端口、项目名称等信息提取出来，单独配置，各产品开发人员单独维护、方便扩展。* @author      : dwg* @date        : Sep 22, 2013 3:12:01 PM *  */public interface HostConfig {	/**	 * 	* @Description: 获取不同系统的主机名称，包括IP和端口信息。示例：http://127.0.0.1:8080	* @param      : @return    	* @return     : String   	* @throws	 */	public String getHost();		/**	 * 	* @Description: 获取部署的项目名称。示例：itims，itsm	* @param      : @return    	* @return     : String   	* @throws	 */	public String getProjectName();		/**	 * 	* @Description: 获取完整的URL 地址。例如：http://127.0.0.1:8080/itsm	* @param      : @return    	* @return     : String   	* @throws	 */	public String getAPIHost();	}