package smart.api.host;public class MonitorSystemHostConfig implements HostConfig {	private String host = "http://127.0.0.1:8080";	private String projectName = "itims";	@Override	public String getHost() {		return this.host;	}	@Override	public String getProjectName() {		return this.projectName;	}	@Override	public String getAPIHost() {		StringBuilder sb = new StringBuilder(host).append("/").append(projectName);		return sb.toString();	}}