package smart.old.api.host;/** * @ClassName   : HostConfigContext * @Description : 主机配置包装类，用于适配不同项目、多个系统之间的主机设置的差异性* @author      : dwg* @date        : Sep 22, 2013 3:23:23 PM *  */public class HostConfigContext {	private HostConfig config;		public HostConfigContext(HostConfig config) {		this.config = config;	}		public HostConfig getConfig() {		return config;	}	public void setConfig(HostConfig config) {		this.config = config;	}			public String getAPIHost(){			return config.getAPIHost();			}}